import { green } from '@material-ui/core/colors';
import React from 'react';
const Img_API = "https://image.tmdb.org/t/p/original";
const setVoteClass=(vote)=>{
    if(vote>8){
        return 'green';
    }
    else if(vote>6){
        return "orange";
    }
    else{
        return "red";
    }
}
const default_img="https://images.unsplash.com/photo-1594908900066-3f47337549d8?ixid=MXwxMjA3fDB8MHxzZWFyY2h8Mnx8bW92aWV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60"

const Movie = (props) => {
    return (
        <>
            <div className="movie">
                <img src={props.img?Img_API + props.img:default_img} alt='No Image Available' />
                <div className="movie_info">
                    <h3>{props.ttl}</h3>
                    <span className={`tag ${setVoteClass(props.v_avg)}`} >Rating : {props.v_avg}</span>
                </div>
                <div className="movie_over">
                    <h2>Overview:</h2>
                    <p>{props.ov}</p>
                </div>
            </div>
        </>
    )
}

export default Movie;
