import React, { useEffect, useState } from 'react';
import Movie from './Movie_Component/Movie';

const Featured_API = "https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=10915b08634caf1b4b082b5eeccc207c&page=1";
const f_API = "https://api.themoviedb.org/3/movie/550?api_key=10915b08634caf1b4b082b5eeccc207c&page=1";
const Search_API = "https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query";

function App() {
  const [movies, setMovies] = useState([]);
  useEffect(() => {
    fetch(Featured_API)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
  }, [])
  return (
    <>
      <div className='movie_container'>
        <header className='search'>
          <input type='text' placeholder='search...' />
        </header>
        {movies.length > 0 && movies.map((movie) => {
          return <Movie key={movie.id} img={movie.poster_path} ttl={movie.title} v_avg={movie.vote_average} ov={movie.overview} {...movie} />
        })}
      </div>
    </>
  );
}

export default App;
