import React, { useState } from 'react';
import InputCss from './InputCss.css';
const Input = () => {
    const [sState, setSStaate] = useState({
        name: '',
        Lname: '',
    });
    const tookName = (event) => {
        console.log(event.target.value);
        const name = event.target.name;
        const value = event.target.value;
        setSStaate((preValue) => {
            if (name === 'name') {
                return {
                    name: value,
                    Lname: preValue.Lname,
                };
            }
            else if (name === 'Lname') {
                return {
                    name: preValue.name,
                    Lname: value,
                };
            }

        })
    }


    const onSubmits = (event) => {
        event.preventDefault();
    }
    return (
        <>
            <div className='main_div'>
                <form onSubmit={onSubmits}>
                    <div>
                        <h1>Hello {sState.name} {sState.Lname}</h1>
                        <input type='text'
                            name='name'
                            placeholder='Enter Your Name'
                            onChange={tookName}
                            // value={name}
                             />
                        <br />
                        <input type='text'
                            name='Lname'
                            placeholder='Enter Your Last Name'
                            onChange={tookName}
                            // value={Lname}
                             />
                        <button type='submit'>SUBMIT</button>
                    </div>
                </form>
            </div>
        </>
    );
};

export default Input;
