import React, { useState } from 'react';
import InputCss from './InputCss.css';
 const Input=()=>{
     const [name ,setName]=useState("");
     const tookName=(event)=>{
        setName(event.target.value);
     }
     return (
        <>
            <div>
     <h1>Hello {name}</h1>
                <input type='text' placeholder='Enter Your Name' onChange={tookName}></input>
                <button>SUBMIT</button>
            </div>      
        </>
     );
 };

 export default Input;
