import React,{useState} from 'react';

// let [count,setCount]=useState(0);


const StateApp=()=>{
    const currT=new Date();
    const [count,setCount]=useState(`${currT.getHours}:${currT.getMinutes}:${currT.getSeconds} AM`);
    const IncNum=()=>{
        setCount(count+1);

    }
    return(
         <div className="top">
             <h1 className='heading'>{count}</h1>
             <button onClick={IncNum} className='btn'>Increment</button>
         </div>
    )
};

export default StateApp;
