import React, { useState } from 'react';
import InputCss from './InputCss.css';
const Input = () => {
    const [sState, setSStaate] = useState({
        name: '',
        Lname: '',
        email:'',
        phone:'',
    });
    const tookName = (event) => {
        console.log(event.target.value);
        const {name,value} = event.target;
        setSStaate((preValue) => {
            console.log(preValue);

        })
    }


    const onSubmits = (event) => {
        event.preventDefault();
    }
    return (
        <>
            <div className='main_div'>
                <form onSubmit={onSubmits}>
                    <div>
                        <h1>Hello {sState.name} {sState.Lname}</h1>
                        <p>{sState.email}</p>
                        <p>{sState.phone}</p>
                        <input type='text'
                            name='name'
                            placeholder='Enter Your Name'
                            onChange={tookName}
                            value={sState.name}
                             />
                        <input type='text'
                            name='Lname'
                            placeholder='Enter Your Last Name'
                            onChange={tookName}
                            value={sState.Lname}
                             />
                        <input type='text'
                            name='email'
                            placeholder='Enter Your Email Adress'
                            onChange={tookName}
                            value={sState.email}
                             />
                        <input type='number'
                            name='phone'
                            placeholder='Enter Your Phone Number'
                            onChange={tookName}
                            value={sState.phone}
                             />
                        <button type='submit'>SUBMIT</button>
                    </div>
                </form>
            </div>
        </>
    );
};

export default Input;
