import React, { useState } from 'react';
import InputCss from './InputCss.css';
const Input = () => {
    const [name, setName] = useState(" ");
    const [nNAME, uName] = useState(" ");
    const [lName, setLName] = useState(" ");
    const [lnNAME, luName] = useState(" ");
    const tookName = (event) => {
        console.log(event.target.value);
        setName(event.target.value);
    }
    const tookName2 = (event) => {
        console.log(event.target.value);
        setLName(event.target.value);
    }
    const updateName = () => {
        uName(name);
        luName(lName);
    }
    return (
        <>
            <div>
                <h1>Hello {nNAME} {lnNAME}</h1>
                <input type='text' placeholder='Enter Your Name' onChange={tookName} />
                <input type='text' placeholder='Enter Your LName' onChange={tookName2} />
                <button onClick={updateName}>SUBMIT</button>
            </div>
        </>
    );
};

export default Input;
