import React, { useState } from 'react';
import InputCss from './InputCss.css';
 const Input=()=>{
     const [name ,setName]=useState(" ");
     const [nNAME,uName]=useState(" ");
     const tookName=(event)=>{
         console.log(event.target.value);
        setName(event.target.value);
     }
     const updateName=()=>{
         uName(name);
     }
     return (
        <>
            <div>
     <h1>Hello {nNAME}</h1>
                <input type='text' placeholder='Enter Your Name' onChange={tookName}/>
                <button onClick={updateName}>SUBMIT</button>
            </div>      
        </>
     );
 };

 export default Input;
