import React,{useState} from 'react';

// let [count,setCount]=useState(0);


const StateApp=()=>{
    const currT=new Date().toLocaleTimeString();
    const [time,setTime]=useState(currT);
    const IncNum=()=>{
        const currTN=new Date().toLocaleTimeString();
        // const currTN=new Date().toLocaleTimeString();
        setTime(currTN);

    }
    return(
         <div className="top">
             <h1 className='heading'>{time}</h1>
             <button onClick={IncNum} className='btn'>Increment</button>
         </div>
    )
};

export default StateApp;
