import React, { useState } from 'react';
import InputCss from './InputCss.css';
const Input = () => {
    const [sState, setSStaate] = useState({
        name: '',
        Lname: '',
        email:'',
        phone:'',
    });
    const tookName = (event) => {
        console.log(event.target.value);
        const name = event.target.name;
        const value = event.target.value;
        setSStaate((preValue) => {
            if (name === 'name') {
                return {
                    name: value,
                    Lname: preValue.Lname,
                    email:preValue.email,
                    phone:preValue.phone,
                };
            }
            else if (name === 'Lname') {
                return {
                    name: preValue.name,
                    Lname: value,
                    email:preValue.email,
                    phone:preValue.phone,
                };
            }
            else if (name === 'email') {
                return {
                    name: preValue.name,
                    Lname: preValue.Lname,
                    email:value,
                    phone:preValue.phone,
                };
            }
            else if (name === 'phone') {
                return {
                    name: preValue.name,
                    Lname: preValue.Lname,
                    email:preValue.email,
                    phone:value,
                };
            }

        })
    }


    const onSubmits = (event) => {
        event.preventDefault();
    }
    return (
        <>
            <div className='main_div'>
                <form onSubmit={onSubmits}>
                    <div>
                        <h1>Hello {sState.name} {sState.Lname}</h1>
                        <p>{sState.email}</p>
                        <p>{sState.phone}</p>
                        <input type='text'
                            name='name'
                            placeholder='Enter Your Name'
                            onChange={tookName}
                            value={sState.name}
                             />
                        <br />
                        <input type='text'
                            name='Lname'
                            placeholder='Enter Your Last Name'
                            onChange={tookName}
                            value={sState.Lname}
                             />
                        <input type='text'
                            name='email'
                            placeholder='Enter Your Email Adress'
                            onChange={tookName}
                            value={sState.email}
                             />
                        <input type='text'
                            name='phone'
                            placeholder='Enter Your Phone Number'
                            onChange={tookName}
                            value={sState.phone}
                             />
                        <button type='submit'>SUBMIT</button>
                    </div>
                </form>
            </div>
        </>
    );
};

export default Input;
