import React, { useEffect, useState } from 'react';
import Movie from './Movie_Component/Movie';

const Featured_API = "https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=10915b08634caf1b4b082b5eeccc207c&page=1";
const Search_API = "https://api.themoviedb.org/3/search/movie?&api_key=10915b08634caf1b4b082b5eeccc207c&query=";

function App() {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const getMovies = (API) => {
    fetch(API)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
  }
  const handlerOnSubmit = (e) => {
    e.preventDefault();

    if (searchTerm) {
      getMovies(Search_API+searchTerm);
      setSearchTerm('');
  }
}
const handlerOnChange = (e) => {
  setSearchTerm(e.target.value)
}

useEffect(() => {
    getMovies(Featured_API);
}, [])
return (
  <>
    <header className='search'>
      <form onSubmit={handlerOnSubmit}>
        <input className='input'
          type='search'
          value={searchTerm}
          onChange={handlerOnChange}
          placeholder='Search...' />
      </form>
    </header>
    <div className='movie_container'>
      {movies.length > 0 && movies.map((movie) => {
        return <Movie key={movie.id} img={movie.poster_path} ttl={movie.title} v_avg={movie.vote_average} ov={movie.overview} {...movie} />
      })}
    </div>
  </>
);
}

export default App;
