import React,{useState} from 'react';

// let [count,setCount]=useState(0);


const StateApp=()=>{
    const currT=new Date();
    const [time,setTime]=useState(`${currT.getHours()}:${currT.getMinutes()}:${currT.getSeconds()} AM`);
    const IncNum=()=>{
        const currTN=new Date();
        const md='AM';
        if(currTN.getHours()>11){
              md='PM';
        }
        else{
            md='AM';
        }
        setTime(`${currTN.getHours()}:${currTN.getMinutes()}:${currTN.getSeconds()} ${md}`);

    }
    return(
         <div className="top">
             <h1 className='heading'>{time}</h1>
             <button onClick={IncNum} className='btn'>Increment</button>
         </div>
    )
};

export default StateApp;
