import React from 'react';
const Img_API = "https://image.tmdb.org/t/p/original";

const Movie = (props) => {
    return (
        <>
            <div className="movie">
                <img src={Img_API + props.img} alt='No Image Available' />
                <div className="movie_info">
                    <h3>{props.ttl}+</h3>
                    <span>Rating : {props.v_avg}</span>
                </div>
                <div className="movie_over">
                    <h2>Overview:</h2>
                    <p>{props.ov}</p>
                </div>
            </div>
        </>
    )
}

export default Movie;
