import React, { useState } from 'react';
import InputCss from './InputCss.css';
const Input = () => {
    const [name, setName] = useState(" ");
    const [nNAME, uName] = useState(" ");
    const [Lname, setLName] = useState(" ");
    const [nLNAME, uLName] = useState(" ");
    const tookName = (event) => {
        console.log(event.target.value);
        setName(event.target.value);
    }
    const tookName2 = (event) => {
        console.log(event.target.value);
        setLName(event.target.value);
    }

    const onSubmits = (event) => {
        event.preventDefault();
        uName(name);
        uLName(Lname);
    }
    return (
        <>
            <div className='main_div'>
                <form onSubmit={onSubmits}>
                    <div>
                        <h1>Hello {nNAME} {nLNAME}</h1>
                        <input type='text' placeholder='Enter Your Name' onChange={tookName} value={name} />
                        <br />
                        <input type='text' placeHolder='Enter Your Last Name' onChange={tookName2} value={Lname} />
                        <button type='submit'>SUBMIT</button>
                    </div>
                </form>
            </div>
        </>
    );
};

export default Input;
